%check_calibrate.mload clrdataMcalmap=ones(256, 3);for i=1:256   calmap(i,:)=(i/256)*[1 1 1 ];end;colormap([gun_v; gun_v;  gun_v]'./256);%put up a square for calibrating;hold off;sr=127*ones(600,800);t=ones(400, 400);for i=5:60:256    replay=0;   rect=t*LtoP(i);   sr((600/2)-200+1:(600/2)+200, (800/2)-200+1:(800/2)+200)=rect;   im=image(sr);    axis off   set(im,'Clipping','off');      save_button=uicontrol('Style','pushbutton', 'Position',[60 131 40 40], ...      'String', int2str(i), 'Callback','replay=1;');   while (replay==0)      pause(1);   end;   end;close all;